      s7_uint x, c;

      s7_uint inline next() {
	const s7_uint result = x; // Or, result = x ^ (x << 32) (see above)
	const __uint128_t t = MWC_A1 * (__uint128_t)x + c;
	x = t;
	c = t >> 64;
	return result;
      }
#endif

