/* -------------------------------- get-output-string -------------------------------- */
const char *s7_get_output_string(s7_scheme *sc, s7_pointer port)
{
  port_data(port)[port_position(port)] = '\0';
  return((const char *)port_data(port));
}

s7_pointer s7_output_string(s7_scheme *sc, s7_pointer port)
{
  port_data(port)[port_position(port)] = '\0';
  if (port_position(port) == 0) return(nil_string);
  return(make_string_with_length(sc, (const char *)port_data(port), port_position(port)));
}

static inline void check_get_output_string_port(s7_scheme *sc, s7_pointer port)
{
  if (port_is_closed(port))
    wrong_type_error_nr(sc, sc->get_output_string_symbol, 1, port, wrap_string(sc, "an active (open) string port", 28));
  if (port_position(port) > sc->max_string_length)
    error_nr(sc, sc->out_of_range_symbol,
	     set_elist_3(sc, wrap_string(sc, "get-output-string port-position ~D is greater than (*s7* 'max-string-length), ~D", 80),
			 wrap_integer(sc, port_position(port)), wrap_integer(sc, sc->max_string_length)));
}
/* if pos>max and clear, where should the clear be?  Not here because we might want to see output in error handler.
 *   similarly below if pos>size how can we call make_string (out-of-bounds) and ignore error?
 *   if pos>size shouldn't we raise an error somewhere?
 */

static s7_pointer g_get_output_string(s7_scheme *sc, s7_pointer args)
{
  #define H_get_output_string "(get-output-string port (clear-port #f)) returns the output accumulated in port.  \
If the optional 'clear-port' is #t, the current string is flushed."
  #define Q_get_output_string s7_make_signature(sc, 3, sc->is_string_symbol, \
                                 s7_make_signature(sc, 2, sc->is_output_port_symbol, sc->not_symbol), sc->is_boolean_symbol)
  s7_pointer port;
  bool clear_port = false;
  if (is_pair(cdr(args)))
    {
      s7_pointer clear = cadr(args);
      if (!is_boolean(clear))
	wrong_type_error_nr(sc, sc->get_output_string_symbol, 2, clear, sc->type_names[T_BOOLEAN]);
      clear_port = (clear == sc->T);
    }
  port = car(args);
  if ((!is_output_port(port)) || (!is_string_port(port)))
    {
      if (port == sc->F) return(nil_string);
      if_method_exists_return_value(sc, port, sc->get_output_string_symbol, args);
      wrong_type_error_nr(sc, sc->get_output_string_symbol, 1, port, wrap_string(sc, "an open string output port or #f", 32));
    }
  check_get_output_string_port(sc, port);

  if ((clear_port) &&
      (port_position(port) < port_data_size(port)))
    {
      block_t *block;
      s7_pointer result = block_to_string(sc, port_data_block(port), port_position(port));
      /* this is slightly faster than make_string_with_length(sc, (char *)(port_data(port)), port_position(port)): we're trading a mallocate for a memcpy */
      port_data_size(port) = sc->initial_string_port_length;
      block = inline_mallocate(sc, port_data_size(port));
      port_data_block(port) = block;
      port_data(port) = (uint8_t *)(block_data(block));
      port_position(port) = 0;
      port_data(port)[0] = '\0';
      return(result);
    }
  if (port_position(port) == 0) return(nil_string);
  return(make_string_with_length(sc, (const char *)port_data(port), port_position(port)));
}

static void op_get_output_string(s7_scheme *sc)
{
  const s7_pointer port = sc->code;
  if (!is_output_port(port))
    wrong_type_error_nr(sc, sc->with_output_to_string_symbol, 1, port, wrap_string(sc, "an open string output port", 26));
  check_get_output_string_port(sc, port);

  /* nil_string here is tricky (need liberate etc) */
  if (port_position(port) >= port_data_size(port)) /* can the > part happen? */
    sc->value = block_to_string(sc, reallocate(sc, port_data_block(port), port_position(port) + 1), port_position(port));
  else sc->value = block_to_string(sc, port_data_block(port), port_position(port));
  /* block_to_string attaches the port's data_block to the string for later free */
  port_data(port) = NULL;
  port_data_size(port) = 0;
  port_data_block(port) = NULL;
  port_needs_free(port) = false;
}

static s7_pointer g_get_output_string_uncopied(s7_scheme *sc, s7_pointer args)
{
  const s7_pointer port = car(args);
  if ((!is_output_port(port)) || (!is_string_port(port)))
    {
      if (port == sc->F) return(nil_string);
      return(method_or_bust_p(sc, port, sc->get_output_string_symbol, wrap_string(sc, "an output string port", 21)));
    }
  check_get_output_string_port(sc, port);
  port_data(port)[port_position(port)] = '\0'; /* wrap_string can't do this, and (for example) open_input_string wants terminated strings */
  if (port_position(port) == 0) return(nil_string);
  return(wrap_string(sc, (const char *)port_data(port), port_position(port)));
}


