static s7_pointer make_hash_table_procedures(s7_scheme *sc)
{
  s7_pointer p = cons(sc, sc->T, sc->T); /* checker, mapped */
  set_opt1_any(p, sc->T);                /* key */
  set_opt2_any(p, sc->T);                /* value */
  return(p);
}

static s7_pointer copy_hash_table_procedures(s7_scheme *sc, s7_pointer table)
{
  if (is_pair(hash_table_procedures(table)))
    {
      s7_pointer p = cons(sc, hash_table_procedures_checker(table), hash_table_procedures_mapper(table));
      set_opt1_any(p, hash_table_key_typer(table));
      set_opt2_any(p, hash_table_value_typer(table));
      return(p);
    }
  return(sc->nil);
}

static void check_hash_table_typer(s7_scheme *sc, s7_pointer caller, s7_pointer table, s7_pointer typer)
{
  if (is_c_function(typer))
    {
      s7_pointer sig = c_function_signature(typer);
      if ((sig != sc->pl_bt) &&
	  (is_pair(sig)) &&
	  ((car(sig) != sc->is_boolean_symbol) || (cadr(sig) != sc->T) || (!is_null(cddr(sig)))))
	wrong_type_error_nr(sc, caller, 2, typer, wrap_string(sc, "a boolean procedure", 19));
      if (!c_function_name(typer))
	wrong_type_error_nr(sc, caller, 2, typer, wrap_string(sc, "a named function", 16));
    }
  else
    {
      if (!is_any_closure(typer))
	wrong_type_error_nr(sc, caller, 2, typer, wrap_string(sc, "a built-in procedure, a closure or #t", 37));
      if (!is_symbol(find_closure(sc, typer, closure_let(typer))))
	wrong_type_error_nr(sc, caller, 2, typer, wrap_string(sc, "a named function", 16));
    }
  if (!s7_is_aritable(sc, typer, 1))
    error_nr(sc, sc->wrong_type_arg_symbol,
	     set_elist_3(sc, wrap_string(sc, "~A: the second argument, ~S, (the type checker) should accept one argument", 74), caller, typer));
  if (is_c_function(typer))
    {
      if (c_function_has_simple_elements(typer))
	{
	  if (caller == sc->hash_table_value_typer_symbol)
	    set_has_simple_values(table);
	  else
	    {
	      set_has_simple_keys(table);
	      if (symbol_type(c_function_symbol(typer)) != T_FREE)
		set_has_hash_key_type(table);
	    }}}
  if (is_null(hash_table_procedures(table)))
    hash_table_set_procedures(table, make_hash_table_procedures(sc));
  set_is_typed_hash_table(table);
}

static s7_pointer g_set_hash_table_key_typer(s7_scheme *sc, s7_pointer args)
{
  const s7_pointer table = car(args), typer = cadr(args);

  if (!is_hash_table(table))
    wrong_type_error_nr(sc, wrap_string(sc, "set! hash_table-key-typer", 25), 1, table, sc->type_names[T_HASH_TABLE]);
  if (is_immutable_hash_table(table))
    immutable_object_error_nr(sc, set_elist_2(sc, wrap_string(sc, "~S is immutable so its key-typer can't be set!", 46), table));

  if (is_boolean(typer)) /* remove current typer, if any */
    {
      if (is_typed_hash_table(table))
	{
	  hash_table_set_key_typer(table, sc->T);
	  clear_has_simple_keys(table);
	  if (hash_table_value_typer(table) == sc->T) clear_is_typed_hash_table(table);
	}}
  else
    {
      check_hash_table_typer(sc, sc->hash_table_key_typer_symbol, table, typer);
      hash_table_set_key_typer(table, typer);
    }
  return(typer);
}

static s7_pointer g_set_hash_table_value_typer(s7_scheme *sc, s7_pointer args)
{
  const s7_pointer table = car(args), typer = cadr(args);

  if (!is_hash_table(table))
    wrong_type_error_nr(sc, wrap_string(sc, "set! hash_table-value-typer", 27), 1, table, sc->type_names[T_HASH_TABLE]);
  if (is_immutable_hash_table(table))
    immutable_object_error_nr(sc, set_elist_2(sc, wrap_string(sc, "~S is immutable so its value-typer can't be set!", 48), table));

  if (is_boolean(typer)) /* remove current typer, if any */
    {
      if (is_typed_hash_table(table))
	{
	  hash_table_set_value_typer(table, sc->T);
	  clear_has_simple_values(table);
	  if (hash_table_key_typer(table) == sc->T) clear_is_typed_hash_table(table);
	}}
  else
    {
      check_hash_table_typer(sc, sc->hash_table_value_typer_symbol, table, typer);
      hash_table_set_value_typer(table, typer);
    }
  return(typer);
}


