/* ---------------- hash strings ---------------- */
static s7_uint hash_map_string(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  if (string_hash(key) == 0)
    string_hash(key) = raw_string_hash((const uint8_t *)string_value(key), string_length(key));
  return(string_hash(key));
}

static hash_entry_t *hash_string(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  if (is_string(key))
    {
      hash_entry_t *entry;
      const s7_int key_len = string_length(key);
      const s7_uint hash_mask = hash_table_mask(table);
      s7_uint hash;
      const char *key_str = string_value(key);

      if (string_hash(key) == 0)
	string_hash(key) = raw_string_hash((const uint8_t *)string_value(key), string_length(key));
      hash = string_hash(key); /* keep s7_uint */

      if (key_len <= 8)
	{
	  for (entry = hash_table_element(table, hash % hash_mask); entry; entry = hash_entry_next(entry))
	    if ((hash == string_hash(hash_entry_key(entry))) &&
		(key_len == string_length(hash_entry_key(entry))))
	      return(entry);
	}
      else
	for (entry = hash_table_element(table, hash % hash_mask); entry; entry = hash_entry_next(entry))
	  if ((hash == string_hash(hash_entry_key(entry))) &&
	      (key_len == string_length(hash_entry_key(entry))) &&        /* these are scheme strings, so we can't assume 0=end of string */
	      (strings_are_equal_with_length(key_str, string_value(hash_entry_key(entry)), key_len)))
	    return(entry);
    }
  return(sc->unentry);
}

