/* -------------------------------- dilambda? -------------------------------- */
bool s7_is_dilambda(s7_pointer func)
{
  if (has_closure_let(func))
    return(is_any_procedure(closure_setter_or_map_list(func))); /* type >= T_CLOSURE (excludes goto/continuation) */
  if (is_any_c_function(func))
    return(is_any_procedure(c_function_setter(func)));          /* type >= T_C_FUNCTION_STAR */
  if (is_c_macro(func))
    return(is_any_procedure(c_macro_setter(func)));
  return(false);
}

