static bool is_fxable(s7_scheme *sc, s7_pointer expr)
{
  if (!is_pair(expr)) return(true);
  if ((is_optimized(expr)) &&  /* this is needed.  In check_tc, for example, is_fxable can be confused by early optimize_op  */
      (fx_function[optimize_op(expr)]))
    return(true);
  return(is_proper_quote(sc, expr));
}

static int32_t fx_count(s7_scheme *sc, s7_pointer expr)
{
  int32_t count = 0;
  for (s7_pointer p = cdr(expr); is_pair(p); p = cdr(p))
    if (is_fxable(sc, car(p)))
      count++;
  return(count);
}

