#define stack_top4_op(Sc)             ((opcode_t)T_Op(Sc->stack_end[-5])) /* top4 == top - 4 */
#define stack_top4_args(Sc)           (Sc->stack_end[-6])
/* #define stack_top4_let(Sc)         (Sc->stack_end[-7]) */
