static /* inline */ void op_closure_fa(s7_scheme *sc) /* "inline" matters perhaps in texit.scm */
{
  s7_pointer new_clo;
  const s7_pointer code = sc->code;
  const s7_pointer farg = opt2_pair(code);             /* cdadr(code), '((a . b) (cons a b)) for (lambda (a . b) (cons a b)) */
  const s7_pointer aarg = fx_call(sc, cddr(code));
  const s7_pointer func = opt1_lambda(code);           /* outer func */
  const s7_pointer func_pars = closure_pars(func);     /* outer func pars (not the arglist of the applied func) */
  sc->value = inline_make_let_with_two_slots(sc, closure_let(func), car(func_pars), sc->F, cadr(func_pars), aarg);
  new_clo = make_closure_unchecked(sc, car(farg), cdr(farg), T_CLOSURE | ((!s7_is_proper_list(sc, car(farg))) ? T_COPY_ARGS : 0), CLOSURE_ARITY_NOT_SET);
  /* this is checking the called closure arglist (see op_lambda), arity<0 probably not usable since "f" in "fa" is a parameter */
  slot_set_value(let_slots(sc->value), new_clo);       /* this order allows us to use make_closure_unchecked */
  set_curlet(sc, sc->value);
  sc->code = car(closure_body(func));
}

static void op_safe_closure_ns(s7_scheme *sc)
{
  s7_pointer args = cdr(sc->code);
  const s7_pointer func = opt1_lambda(sc->code);
  const s7_pointer let = closure_let(func);
  const s7_int id = ++sc->let_number;
  let_set_id(let, id);
  for (s7_pointer slot = let_slots(let); tis_slot(slot); slot = next_slot(slot), args = cdr(args))
    {
      slot_set_value(slot, lookup(sc, car(args)));
      symbol_set_local_slot(slot_symbol(slot), id, slot);
    }
  set_curlet(sc, let);
  sc->code = closure_body(func);
  if_pair_set_up_begin_unchecked(sc);
}

static inline void op_safe_closure_3a(s7_scheme *sc)
{
  const s7_pointer p = cdr(sc->code);
  const s7_pointer func = opt1_lambda(sc->code);
  sc->code = fx_call(sc, cdr(p));   /* fx_call can affect sc->value, but not sc->code, I think */
  sc->args = fx_call(sc, cddr(p));  /* is sc->args safe here? */
  set_curlet(sc, update_let_with_three_slots(sc, closure_let(func), fx_call(sc, p), sc->code, sc->args));
  sc->code = closure_body(func);
  if_pair_set_up_begin_unchecked(sc);
}

static void op_safe_closure_na(s7_scheme *sc)
{
  s7_pointer let;
  s7_int id;

  sc->args = safe_list_if_possible(sc, opt3_arglen(cdr(sc->code)));
  for (s7_pointer args = cdr(sc->code), p = sc->args; is_pair(args); args = cdr(args), p = cdr(p))
    set_car(p, fx_call(sc, args));
  sc->code = opt1_lambda(sc->code);
  id = ++sc->let_number;
  let = closure_let(sc->code);
  let_set_id(let, id);
  for (s7_pointer slot = let_slots(let), vals = sc->args; tis_slot(slot); slot = next_slot(slot), vals = cdr(vals))
    {
      slot_set_value(slot, car(vals));
      symbol_set_local_slot(slot_symbol(slot), id, slot);
    }
  if (!in_heap(sc->args)) clear_safe_list_in_use(sc->args);
  set_curlet(sc, let);
  sc->code = closure_body(sc->code);
  if_pair_set_up_begin_unchecked(sc);
}

static /* inline */ void op_closure_ns(s7_scheme *sc) /* called once in eval, lg? */
{
  /* in this case, we have just lambda (not lambda*), and no dotted arglist,
   *   and no accessed symbols in the arglist, and we know the arglist matches the parameter list.
   */
  s7_pointer args = cdr(sc->code), last_slot;
  const s7_pointer func = opt1_lambda(sc->code);
  const s7_pointer pars = closure_pars(func);
  const s7_pointer e = inline_make_let(sc, closure_let(func));
  const s7_int id = let_id(e);
  begin_temp(sc->y, e);
  add_slot_unchecked(sc, e, car(pars), lookup(sc, car(args)), id);
  last_slot = let_slots(e);
  args = cdr(args);
  for (s7_pointer p1 = cdr(pars); is_pair(p1); p1 = cdr(p1), args = cdr(args))
    last_slot = add_slot_at_end(sc, id, last_slot, car(p1), lookup(sc, car(args))); /* main such call in lt (fx_s is 1/2, this is 1/5 of all calls) */
  set_curlet(sc, e);
  end_temp(sc->y);
  sc->code = T_Pair(closure_body(func));
  if_pair_set_up_begin(sc);
}

