static void op_closure_na(s7_scheme *sc)
{
  const s7_pointer exprs = cdr(sc->code);         /* "n" = opt3_arglen(exprs), mostly 5 in lt, 6 in tlet */
  const s7_pointer func = opt1_lambda(sc->code);
  const s7_pointer pars = closure_pars(func);
  const s7_pointer e = inline_make_let(sc, closure_let(func));
  s7_pointer slot, last_slot;
  sc->z = e;
  sc->value = fx_call(sc, exprs);
  new_cell_no_check(sc, last_slot, T_SLOT);
  slot_set_symbol_and_value(last_slot, car(pars), sc->value);
  slot_set_next(last_slot, let_slots(e));   /* i.e. slot_end */
  let_set_slots(e, last_slot);
  for (s7_pointer par = cdr(pars), expr = cdr(exprs); is_pair(par); par = cdr(par), expr = cdr(expr))
    {
      sc->value = fx_call(sc, expr);        /* before new_cell since it might call the GC */
      new_cell(sc, slot, T_SLOT);           /* args < GC_TRIGGER checked in optimizer, but we're calling fx_call? */
      slot_set_symbol_and_value(slot, car(par), sc->value);
      /* setting up the let might use unrelated-but-same-name symbols, so wait to set the symbol ids */
      slot_set_next(slot, slot_end);
      slot_set_next(last_slot, slot);
      last_slot = slot;
    }
  set_curlet(sc, e);
  sc->z = sc->unused;
  let_set_id(e, ++sc->let_number);
  for (s7_pointer slot1 = let_slots(e); tis_slot(slot1); slot1 = next_slot(slot1))
    {
      symbol_set_local_slot(slot_symbol(slot1), let_id(e), slot1);
      set_local(slot_symbol(slot1));
    }
  sc->code = T_Pair(closure_body(func));
  if_pair_set_up_begin(sc);
}

static bool check_closure_sym(s7_scheme *sc, int32_t args)
{
  /* can't use closure_is_fine -- (lambda args 1) and (lambda (name . args) 1) are both arity -1 for the internal arity checkers! */
  if ((symbol_ctr(car(sc->code)) != 1) ||
      (unchecked_local_value(car(sc->code)) != opt1_lambda_unchecked(sc->code)))
    {
      const s7_pointer func = lookup_unexamined(sc, car(sc->code));
      if ((func != opt1_lambda_unchecked(sc->code)) &&
	  ((!func) ||
	   ((low_type_bits(func) & (TYPE_MASK | T_SAFE_CLOSURE)) != T_CLOSURE) ||
	   (((args == 1) && (!is_symbol(closure_pars(func)))) ||
	    ((args == 2) && ((!is_pair(closure_pars(func))) || (!is_symbol(cdr(closure_pars(func)))))))))
	{
	  sc->last_function = func;
	  return(false);
	}
      set_opt1_lambda(sc->code, func);
    }
  return(true);
}

static void op_any_closure_sym(s7_scheme *sc) /* for (lambda a ...) */
{
  const s7_pointer func = opt1_lambda(sc->code);
  s7_pointer old_args = cdr(sc->code); /* args aren't evaluated yet */
  const s7_int num_args = opt3_arglen(old_args);

  if (num_args == 1)
    set_curlet(sc, inline_make_let_with_slot(sc, closure_let(func), closure_pars(func),
					     ((is_safe_closure(func)) && (!sc->debug_or_profile)) ?
					     set_plist_1(sc, fx_call(sc, old_args)) : list_1(sc, sc->value = fx_call(sc, old_args))));
  else
    if (num_args == 2)
      {
	gc_protect_via_stack(sc, fx_call(sc, old_args)); /* not sc->value as GC protection! -- fx_call below can clobber it */
	sc->args = fx_call(sc, cdr(old_args));
	set_curlet(sc, inline_make_let_with_slot(sc, closure_let(func), closure_pars(func),
					         ((is_safe_closure(func)) && (!sc->debug_or_profile)) ?
					         set_plist_2(sc, gc_protected1(sc), sc->args) : list_2(sc, gc_protected1(sc), sc->args)));
	unstack_gc_protect(sc);
      }
    else
      if (num_args == 0)
	set_curlet(sc, inline_make_let_with_slot(sc, closure_let(func), closure_pars(func), sc->nil));
      else
	{
	  sc->args = make_list(sc, num_args, sc->unused);
	  for (s7_pointer p = sc->args; is_pair(p); p = cdr(p), old_args = cdr(old_args))
	    set_car(p, fx_call(sc, old_args));
	  set_curlet(sc, make_let_with_slot(sc, closure_let(func), closure_pars(func), sc->args));
	}
  sc->code = T_Pair(closure_body(func));
}

static void op_any_closure_a_sym(s7_scheme *sc) /* for (lambda (a . b) ...) */
{
  const s7_pointer func = opt1_lambda(sc->code);
  s7_pointer old_args = cdr(sc->code);
  const s7_int num_args = opt3_arglen(old_args);
  const s7_pointer func_pars = closure_pars(func);

  if (num_args == 1)
    set_curlet(sc, make_let_with_two_slots(sc, closure_let(func), car(func_pars), sc->value = fx_call(sc, old_args), cdr(func_pars), sc->nil));
  else
    {
      gc_protect_via_stack(sc, fx_call(sc, old_args)); /* not sc->value as GC protection! -- fx_call below can clobber it */
      if (num_args == 2)
	{
	  sc->args = fx_call(sc, cdr(old_args));
	  set_curlet(sc, inline_make_let_with_two_slots(sc, closure_let(func), car(func_pars), gc_protected1(sc), cdr(func_pars), list_1(sc, sc->args)));
	}
      else
	{
	  sc->args = make_list(sc, num_args - 1, sc->unused);
	  old_args = cdr(old_args);
	  for (s7_pointer p = sc->args; is_pair(p); p = cdr(p), old_args = cdr(old_args))
	    set_car(p, fx_call(sc, old_args));
	  set_curlet(sc, make_let_with_two_slots(sc, closure_let(func), car(func_pars), gc_protected1(sc), cdr(func_pars), sc->args));
	}
      unstack_gc_protect(sc);
    }
  sc->code = T_Pair(closure_body(func));
}


