static s7_pointer starlet_set_1(s7_scheme *sc, s7_pointer sym, s7_pointer val)
{
  if (S7_DEBUGGING)
    {
      if (!is_symbol(sym)) {fprintf(stderr, "%s[%d]: %s is not a symbol\n", __func__, __LINE__, display(sym)); if (sc->stop_at_error) abort();}
      if (is_symbol_and_keyword(sym)) {fprintf(stderr, "%s[%d]: %s is a keyword\n", __func__, __LINE__, display(sym)); if (sc->stop_at_error) abort();}
      if (starlet_symbol_id(sym) == sl_no_field) {fprintf(stderr, "%s[%d]: %s is not an *s7* field\n", __func__, __LINE__, display(sym)); if (sc->stop_at_error) abort();}
    }
  switch (starlet_symbol_id(sym))
    {
    case sl_accept_all_keyword_arguments:  return(sl_set_accept_all_keyword_arguments(sc, sym, val));
    case sl_autoloading:                   return(sl_set_autoloading(sc, sym, val));
    case sl_bignum_precision:              return(sl_set_bignum_precision(sc, sym, val));
    case sl_catches:                       sl_unsettable_error_nr(sc, sym);
    case sl_cpu_time:                      sl_unsettable_error_nr(sc, sym);
    case sl_c_types:                       sl_unsettable_error_nr(sc, sym);
    case sl_debug:                         return(sl_set_debug(sc, sym, val));
    case sl_default_hash_table_length:     return(sl_set_default_hash_table_length(sc, sym, val));
    case sl_default_random_state:          return(sl_set_default_random_state(sc, sym, val));
    case sl_default_rationalize_error:     sc->default_rationalize_error = sl_real_geq_0(sc, sym, val); return(val);
    case sl_equivalent_float_epsilon:      sc->equivalent_float_epsilon = sl_real_geq_0(sc, sym, val); return(val);
    case sl_expansions:                    return(sl_set_expansions(sc, sym, val));
    case sl_file_names: case sl_filenames: sl_unsettable_error_nr(sc, sym);
    case sl_float_format_precision:        return(sl_set_float_format_precision(sc, sym, val));
    case sl_free_heap_size:                sl_unsettable_error_nr(sc, sym);
    case sl_gc_freed:                      sl_unsettable_error_nr(sc, sym);
    case sl_gc_total_freed:                sl_unsettable_error_nr(sc, sym);
    case sl_gc_protected_objects:          sl_unsettable_error_nr(sc, sym);
    case sl_gc_temps_size:                 return(sl_set_gc_temps_size(sc, sym, val));
    case sl_gc_resize_heap_fraction:       sc->gc_resize_heap_fraction = sl_real_0_to_1(sc, sym, val); return(val);
    case sl_gc_resize_heap_by_4_fraction:  sc->gc_resize_heap_by_4_fraction = sl_real_0_to_1(sc, sym, val); return(val);
    case sl_gc_stats:                      return(sl_set_gc_stats(sc, sym, val));
    case sl_gc_info:                       return(sl_set_gc_info(sc, sym, val));
    case sl_hash_table_float_epsilon:      sc->hash_table_float_epsilon = sl_real_geq_0(sc, sym, val); return(val);
    case sl_hash_table_missing_key_value:  hash_entry_set_value(sc->unentry, val); return(val);
    case sl_heap_size:                     return(sl_set_heap_size(sc, sym, val));
    case sl_history:                       replace_current_code(sc, val); return(val);
    case sl_history_enabled:               return(sl_set_history_enabled(sc, sym, val));
    case sl_history_size:                  return(sl_set_history_size(sc, sym, val));
    case sl_initial_string_port_length:    return(sl_set_initial_string_port_length(sc, sym, val));
    case sl_iterator_at_end_value:         sc->iterator_at_end_value = val; return(val);
    case sl_major_version:                 sl_unsettable_error_nr(sc, sym);
    case sl_minor_version:                 sl_unsettable_error_nr(sc, sym);
    case sl_max_heap_size:                 return(sl_set_max_heap_size(sc, sym, val));
    case sl_max_list_length:               sc->max_list_length = s7_integer_clamped_if_gmp(sc, sl_integer_gt_0(sc, sym, val)); return(val);
    case sl_max_stack_size:                return(sl_set_max_stack_size(sc, sym, val));
    case sl_max_string_length:             sc->max_string_length = s7_integer_clamped_if_gmp(sc, sl_integer_gt_0(sc, sym, val)); return(val);
    case sl_max_string_port_length:        return(sl_set_max_string_port_length(sc, sym, val));
    case sl_max_vector_dimensions:         sc->max_vector_dimensions = s7_integer_clamped_if_gmp(sc, sl_integer_gt_0(sc, sym, val)); return(val);
    case sl_max_vector_length:             sc->max_vector_length = s7_integer_clamped_if_gmp(sc, sl_integer_gt_0(sc, sym, val)); return(val);
    case sl_memory_usage:                  sl_unsettable_error_nr(sc, sym);
    case sl_most_negative_fixnum:          sl_unsettable_error_nr(sc, sym);
    case sl_most_positive_fixnum:          sl_unsettable_error_nr(sc, sym);
    case sl_muffle_warnings:               return(sl_set_muffle_warnings(sc, sym, val));
    case sl_number_separator:              return(sl_set_number_separator(sc, sym, val));    /* I think no PL uses the separator in output */
    case sl_openlets:                      return(sl_set_openlets(sc, sym, val));
    case sl_output_file_port_length:       sc->output_file_port_length = s7_integer_clamped_if_gmp(sc, sl_integer_gt_0(sc, sym, val)); return(val);
    case sl_print_length:                  sc->print_length = s7_integer_clamped_if_gmp(sc, sl_integer_geq_0(sc, sym, val)); return(val);
    case sl_profile:                       return(sl_set_profile(sc, sym, val));
    case sl_profile_info:                  return(sl_set_profile_info(sc, sym, val));
    case sl_profile_prefix:                return(sl_set_profile_prefix(sc, sym, val));
    case sl_rootlet_size:                  sl_unsettable_error_nr(sc, sym);
    case sl_safety:                        return(sl_set_safety(sc, sym, val));
    case sl_stacktrace_defaults:           return(sl_set_stacktrace_defaults(sc, sym,val));
    case sl_stack:                         sl_unsettable_error_nr(sc, sym);
    case sl_stack_size:                    sl_unsettable_error_nr(sc, sym);
    case sl_stack_top:                     sl_unsettable_error_nr(sc, sym);
    case sl_symbol_printer:                return(sl_set_symbol_printer(sc, sym, val));
    case sl_symbol_quote:                  return(sl_set_symbol_quote(sc, sym, val));
    case sl_undefined_constant_warnings:   return(sl_set_undefined_constant_warnings(sc, sym, val));
    case sl_undefined_identifier_warnings: return(sl_set_undefined_identifier_warnings(sc, sym, val));
    case sl_version:                       sl_unsettable_error_nr(sc, sym);
    default: return(sc->undefined); /* can't happen */
      /* error_nr(sc, sc->out_of_range_symbol, set_elist_2(sc, wrap_string(sc, "can't set (*s7* '~S); no such field in *s7*", 43), sym)); */
    }
  return(sc->undefined);
}

s7_pointer s7_starlet_set(s7_scheme *sc, s7_pointer sym, s7_pointer new_value)
{
  if (is_symbol(sym))
    {
      if (is_keyword(sym))
	sym = keyword_symbol(sym);
      if (starlet_symbol_id(sym) != sl_no_field)
	return(starlet_set_1(sc, sym, new_value));
    }
  return(sc->undefined);
}

s7_pointer s7_let_field_set(s7_scheme *sc, s7_pointer sym, s7_pointer new_value) {return(s7_starlet_set(sc, sym, new_value));}
