#define NUM_INTEGER_WRAPPERS 4
#define NUM_REAL_WRAPPERS 4
#define NUM_COMPLEX_WRAPPERS 4
#define NUM_LET_WRAPPERS 4
#define NUM_SLOT_WRAPPERS 4

